onClick:
{{
(async () => {
  // 0) Reinicia el modal a sus valores por defecto
  resetWidget("Modal_Nuevo", true);

  // 1) Olvida cualquier fila que se estuviera editando
  await storeValue("cliente_edit_row", null, true);

  // 2) Limpia inputs de texto
  nombre_cliente_new.setValue("");
  email_cliente_new.setValue("");
  comentario_cliente.setValue("");
  razon_social.setValue("");
  rfc_facturacion.setValue("");
  domicilio_fiscal.setValue("");

  // 3) Limpia selects / menús
  prefijo_nombre_cliente?.setSelectedOption(null);
  tipo_cliente?.setSelectedOption(null);
  regimen_fiscal?.setSelectedOption(null);
  uso_de_cfdi?.setSelectedOption(null);
  forma_de_pago?.setSelectedOption(null);
  dia_de_corte?.setSelectedOption(null);
  dia_de_corte?.setSelectedOption(null);

  // 4) Switches
  requiere_factura_new?.setValue(false);
  lista_negra_new?.setValue(false);

  // 5) ¡No tocar! id_cliente y estatus_cliente se controlan con bindings

  // 6) Marca el modo "Nuevo cliente"
  nuevo_cliente?.setDisabled(false);
  nuevo_cliente?.setValue(true);

  // 7) Abre el modal
  showModal("Modal_Nuevo");
})();
}}
