onClick:
{{
(() => {
  const trim = (v) => String(v ?? "").trim();
  const has  = (v) => trim(v).length > 0;

  // --- Reglas base (siempre requeridas) ---
  if (!has(nombre_cliente_new.text)) {
    showAlert("Captura el nombre del cliente.", "warning"); return;
  }
	 if (!has(celular_cliente_new.text)) {
    showAlert("Captura el celular del cliente.", "warning"); return;
  }
  if (!dia_de_corte_new.selectedOptionValue) {
    showAlert("Selecciona el día de corte de pago.", "warning"); return;
  }

  // --- Validaciones condicionadas a "requiere_factura" ---
  if (requiere_factura_new.isSwitchedOn) {
    if (!has(razon_social.text))     { showAlert("Captura la Razón Social de facturación.", "warning"); return;
    }
    if (!has(rfc_facturacion.text)) {
      showAlert("Captura el RFC.", "warning"); return;
    }
    if (!has(domicilio_fiscal.text)) 
		{ showAlert("Captura el domicilio fiscal.", "warning"); return;
    }
    if (!has(codigo_postal_rfc.text))    { showAlert("Captura el código postal del domicilio fiscal.", "warning"); return;
    }
    if (!tipo_cliente?.selectedOptionValue) {
showAlert("Captura el tipo de cliente fiscal.", "warning");
return;
}
    if (!regimen_fiscal?.selectedOptionValue) {
showAlert("Captura el regimen fiscal.", "warning");
return;
}
    if (!uso_de_cfdi?.selectedOptionValue) {
showAlert("Captura el uso de cfdi.", "warning");
return;
}
    if (!forma_de_pago?.selectedOptionValue) {
showAlert("Captura la forma de pago.", "warning");
return;
}
    if (!metodo_de_pago?.selectedOptionValue) {
showAlert("Captura el metodo de pago.", "warning");
return;
}
    if (!has(email_factura.text))    { showAlert("Captura el email para recibir la factura.", "warning"); return;
    }
  }



  // --- Todo OK -> abrir confirmación ---
  resetWidget("Modal_confirmar_nuevo", true);
  showModal("Modal_confirmar_nuevo");
})();
}}
