onClick:
{{
(() => {
  const trim = (v) => String(v ?? "").trim();
  const has  = (v) => trim(v).length > 0;

  // --- Reglas base (siempre requeridas) ---
  if (!has(nombre_cliente.text)) {
    showAlert("Captura el nombre del cliente.", "warning"); return;
  }
	 if (!has(celular_cliente.text)) {
    showAlert("Captura el celular del cliente.", "warning"); return;
  }
		 if (!
				 has(email_cliente.text)) {
    showAlert("Captura el email del cliente.", "warning"); return;
  }
  if (!dia_de_corte.selectedOptionValue) {
    showAlert("Selecciona el día de corte de pago.", "warning"); return;
  }

  // --- Validación opcional de email (si viene, que tenga formato válido) ---
  const email = trim(email_cliente.text);
  if (email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
    showAlert("El email no tiene un formato válido.", "warning"); return;
  }

  // --- Todo OK -> abrir confirmación ---
  resetWidget("Modal_confirmar_editar", true);
  showModal("Modal_confirmar_editar");
})();
}}
