onClick:
{{
(async () => {
  // Si es "nuevo cliente", el check es obligatorio
  if (nuevo_cliente.isSwitchedOn && !Chk_entiendo.isChecked) {
    showAlert("Debes aceptar la confirmaci√≥n para crear un nuevo cliente.", "warning");
    return;
  }

  try {
    const creaNuevo = !!nuevo_cliente.isSwitchedOn;

    if (creaNuevo) {
      await InsertClientes.run();
      showAlert("Cliente creado correctamente.", "success");
    } else {
      await UpdateClientes.run();
      showAlert("Cliente actualizado correctamente.", "success");
    }

    // (Opcional) refrescar tabla y cerrar modales
    await GetClientes.run();
    resetWidget("Chk_entiendo", true);
    closeModal("Modal_confirmar_editar");
    closeModal("Modal_Editar");
  } catch (e) {
    showAlert("Error al guardar: " + (e?.message ?? e), "error");
  }
})();
}}
