{{
(() => {
  const d = appsmith.store.nuevoCobroDraft || {};

  const formatDate = (v) => {
    if (!v) return "-";
    const date = new Date(v);
    if (isNaN(date)) return "-";
    const dd = String(date.getDate()).padStart(2, "0");
    const mm = String(date.getMonth() + 1).padStart(2, "0");
    const yyyy = date.getFullYear();
    return `${dd}/${mm}/${yyyy}`;
  };

  const formatMoney = (v) => {
    const n = Number(v);
    if (!Number.isFinite(n)) return "-";
    return n.toLocaleString("es-MX", { minimumFractionDigits: 2, maximumFractionDigits: 2 });
  };

  const periodo = String(d.periodo_cobro ?? d.periodo ?? "").trim() || "-";

  return `
Deseas confirmar la creación de un nuevo cobro con los siguentes datos:

• Pedido: ${d.id_item_pedido || "-"}
• Periodo de cobro: ${periodo}
• Cliente: ${d.nombre_cliente || "-"}
• Ubicación: ${d.nombre_ubicacion || "-"}
• Baño: ${d.id_bano || "-"}
• Fecha inicio: ${formatDate(d.fecha_inicio)}
• Fecha fin: ${formatDate(d.fecha_fin)}
• Costo: $${formatMoney(d.costo)}
`;
})()
}}
