onOptionChange:
{{
(() => {
  const pedidos = GetPedidos.data || [];
  const clientes = GetClientes.data || [];
  const idItem = String(DropdownIdItemNew.selectedOptionValue || "");

  const pedido = pedidos.find(p =>
    String(p.id_item_pedido ?? p._id_item_pedido ?? "") === idItem
  ) || {};

  const idCliente = String(pedido.id_cliente || "");
  const cliente = clientes.find(c =>
    String(c.id_cliente || "") === idCliente
  ) || {};

  // 1) Nombre cliente
  InputNombreClienteNew.setValue(String(cliente.nombre_cliente || ""));

  // 2) Nombre ubicación (desde pedidos)
  InputNombreUbicacionNew.setValue(String(pedido.nombre_ubicacion || ""));

  // 3) Id baño (value)
  IdBanoNew.setValue(String(pedido.id_bano || ""));

  // 4) Fecha inicio
  FechaInicioNew.setValue(String(pedido.fecha_inicio || ""));

  // 5) Fecha fin
  FechaFinNew.setValue(String(pedido.fecha_fin || ""));
})()
}}

sourceData:
{{
  // Siempre devolver la lista completa de items (labels con cliente)
  (GetPedidos.data || []).map(p => {
    const cli = (GetClientes.data || []).find(c => String(c.id_cliente) === String(p.id_cliente));
    const nombreCli = cli ? (cli.nombre_cliente || cli.nombre || "") : (p.nombre_cliente || "");
    return {
      label: `${p.id_item_pedido} — ${nombreCli}`,
      value: p.id_item_pedido,
      _bano: p.id_bano,
      _id_ubicacion: p.id_ubicacion,
      _id_ruta: p.id_ruta
    };
  });
}}
