{{
(() => {
  const cobros = GetCobros.data || [];

  // 1) Periodos existentes en cobros (normalizados a string)
  const periodosCobros = (cobros || [])
    .map(c => c.periodo_cobro)
    .filter(Boolean)
    .map(p => String(p));

  const existentesSet = new Set(periodosCobros);

  // Helpers
  const pad2 = (n) => String(n).padStart(2, "0");
  const mkPeriodo = (y, m, d) => `${y}${pad2(m)}${pad2(d)}`;

  // 2) Calcular SOLO el candidato según el día de hoy
  const now = new Date();
  const y = now.getFullYear();
  const m = now.getMonth() + 1; // 1-12
  const day = now.getDate();

  let candidato;
  if (day >= 1 && day <= 10) {
    candidato = mkPeriodo(y, m, 10);
  } else if (day >= 11 && day <= 25) {
    candidato = mkPeriodo(y, m, 25);
  } else {
    // 26-31 => mes siguiente día 10 (ajusta año si es diciembre)
    const nextM = (m === 12) ? 1 : (m + 1);
    const nextY = (m === 12) ? (y + 1) : y;
    candidato = mkPeriodo(nextY, nextM, 10);
  }

  // 3) Unir: existentes + (candidato solo si no existe)
  const all = [
    ...periodosCobros,
    ...(!existentesSet.has(String(candidato)) ? [String(candidato)] : [])
  ];

  // 4) Quitar duplicados, ordenar desc (más reciente -> más antigua)
  const uniq = Array.from(new Set(all));
  uniq.sort((a, b) => String(b).localeCompare(String(a)));

  // 5) Formato dropdown
  return uniq.map(p => ({ label: String(p), value: String(p) }));
})();
}}
