{{
(() => {
  const selItem = id_item_pedido.selectedOptionValue;
  const pedidos = Array.isArray(GetPedidos.data) ? GetPedidos.data : (GetPedidos.data?.results || []);
  const ubics   = Array.isArray(GetUbicaciones.data) ? GetUbicaciones.data : (GetUbicaciones.data?.results || []);
  const selUbic = nombre_ubicacion.selectedOptionValue;

  // Si hay item seleccionado y trae geo, úsala
  if (selItem) {
    const row = pedidos.find(r => String(r.id_item_pedido) === String(selItem));
    if (row?.geolocalizacion_ubicacion) return row.geolocalizacion_ubicacion;
  }

  // Si no, mira la ubicación elegida en el select de ubicación
  if (selUbic) {
    const u = ubics.find(x => String(x.id_ubicacion) === String(selUbic));
    return u?.geolocalizacion_ubicacion ?? "";
  }

  return "";
})()
}}
