sourceData:
{{
(() => {
  const selItem = id_item_pedido.selectedOptionValue;
  const pedidos = Array.isArray(GetPedidos.data) ? GetPedidos.data : (GetPedidos.data?.results || []);
  const invent = Array.isArray(GetInventario.data) ? GetInventario.data : (GetInventario.data?.results || []);

  if (selItem) {
    const row = pedidos.find(r => String(r.id_item_pedido) === String(selItem));
    if (!row?.id_bano) return [];
    return [{ label: String(row.id_bano), value: String(row.id_bano) }];
  }

  // si NO hay id_item_pedido → mostrar todos los baños "Disponible en almacen"
  return invent
    .filter(b => String(b.estatus_bano || "").trim().toLowerCase() === "disponible en almacen")
    .map(b => ({ label: String(b.id_bano), value: String(b.id_bano) }));
})()
}}
