onClick:
{{
(async () => {
  const act   = actividad_programada.selectedOptionValue || "";
  const item  = id_item_pedido.selectedOptionValue || null;
  const bano  = id_bano.selectedOptionValue || null;
  const ubic  = nombre_ubicacion.selectedOptionValue || null;
  const fecha = fecha_programada.selectedDate || null;

  // Validaciones básicas
  if (!act) { showAlert("Selecciona la actividad", "warning"); return; }
  if (act === "Limpieza cliente" && !item) { showAlert("Selecciona el ID item pedido", "warning"); return; }
  if (!ubic) { showAlert("Selecciona la ubicación", "warning"); return; }
  if (!fecha) { showAlert("Selecciona la fecha programada", "warning"); return; }

  // Construye payload
  const payload = {
    actividad_programada: act,
    id_item_pedido: item,                     // puede ser null para actividades de almacén
    id_bano: bano,
    id_ubicacion: ubic,
    geolocalizacion_ubicacion: geolocalizacion_ubicacion.text || geolocalizacion_ubicacion.value || "",
    id_ruta: id_ruta.text || "",
    costo_reparacion: costo_reparacion.value ?? costo_reparacion.text ?? null,
    id_usuario: appsmith.user?.email || "",
    fecha_creacion_actual: new Date().toISOString(),
    fecha_programada: fecha
  };

  // Ejecuta insert (ajusta si tu API requiere body/query params diferentes)
  await InsertCobros.run(payload);
  showAlert("Orden de trabajo creada", "success");
  closeModal("Modal_Procesar");
})()
}}
