onClick:
{{
(() => {
  const sel = appsmith.store.clienteSel;

  if (sel?.id_cliente) {
    // Hay cliente en store → reflejamos EXISTENTE
    es_cliente_existente.setValue(true);
    es_cliente_nuevo.setValue(false);

    // No pisar lo que el usuario ya escribió manualmente:
    if (!id_cliente.text) {
      id_cliente.setValue(sel.id_cliente);
    }
    if (!nombre_cliente.text) {
      nombre_cliente.setValue(sel.nombre_cliente);
    }
  } else {
    // No hay selección → ambos OFF (ni existente ni nuevo)
    es_cliente_existente.setValue(false);
    es_cliente_nuevo.setValue(false);
    // No limpiamos inputs para no perder lo que el usuario haya escrito
  }

  closeModal("Identificacion_de_cliente");
  showModal("Modal_Procesar");
})()
}}
