onClick:
{{
(() => {
  const id     = (Listado_clientes.selectedOptionValue  || "").trim();
  const nombre = (Listado_clientes.selectedOptionLabel || "").trim();

  if (id) {
    storeValue("clienteSel", { id_cliente: id, nombre_cliente: nombre }, true);
    es_cliente_existente.setValue(true);
    es_cliente_nuevo.setValue(false);
  } else {
    storeValue("clienteSel", null, true);
    es_cliente_existente.setValue(false);
    es_cliente_nuevo.setValue(false);
  }

  closeModal("Identificacion_de_cliente");
  showModal("Modal_Procesar"); // opcional: re-enfoca el modal principal
})()
}}
