onClick:
{{
(() => {
  const servicio     = tipo_de_servicio.selectedOptionValue;
  const formaPago    = forma_de_pago.selectedOptionValue;
  const tipoCliente  = tipo_de_cliente.selectedOptionValue;
  const nombreUbic   = (nombre_ubicacion.text || "").trim();
  const DiadeCorte  = dia_corte_pago.selectedOptionValue;
  const esExistente  = es_cliente_existente.isSwitchedOn;
  const esNuevo      = es_cliente_nuevo.isSwitchedOn;

  const idClienteSel = (appsmith.store.clienteSel?.id_cliente || "").trim();
  const nombreNuevo  = (nombre_cliente.text || "").trim();   // ⟵ NOMBRE CAPTURADO PARA NUEVO

  // 1) Selects/inputs obligatorios
  if (!servicio)    { showAlert("Selecciona el tipo de servicio", "warning"); return; }
  if (!formaPago)   { showAlert("Selecciona la forma de pago", "warning");   return; }
  if (!tipoCliente) { showAlert("Selecciona el tipo de cliente", "warning"); return; }
  if (!nombreUbic)  { showAlert("Captura el nombre de la ubicación", "warning"); return; }
  if (!DiadeCorte)   { showAlert("Selecciona el día de corte de pago", "warning");   return; }

  // 2) Debe estar activo al menos uno de los switches
  if (!esExistente && !esNuevo) {
    showAlert("Indica si es cliente existente o cliente nuevo.", "warning");
    return;
  }

  // 3) Si es EXISTENTE → debe haber uno seleccionado
  if (esExistente && !idClienteSel) {
    showAlert("Selecciona un cliente existente.", "warning");
    return;
  }

  // 4) Si es NUEVO → debe haber nombre capturado
  if (esNuevo && !nombreNuevo) {
    showAlert("Captura el nombre del nuevo cliente.", "warning");
    return;
  }

  // 5) Todo OK → abrir confirmación
  resetWidget("Confirmar_Procesar", true);
  showModal("Confirmar_Procesar");
})()
}}
