text:
En uso
{{
  (
    (GetInventario.data.filter(
      b => b.tipo_bano !== "Externo"
    ).length - 
     GetInventario.data.filter(
       b => b.estatus_bano === "Disponible en almacen" &&
            b.tipo_bano !== "Externo"
     ).length
    ) /
    (GetInventario.data.filter(
      b => b.tipo_bano !== "Externo"
    ).length || 1)
  * 100
  ).toFixed(1) + " %"
}}

textColor:
{{
  (() => {
    const totalSinExternos = GetInventario.data.filter(
      b => b.tipo_bano !== "Externo"
    ).length || 1;
    const disponiblesSinExternos = GetInventario.data.filter(
      b => b.estatus_bano === "Disponible en almacen" &&
           b.tipo_bano !== "Externo"
    ).length;
    const porcentaje = ( (totalSinExternos - disponiblesSinExternos) / totalSinExternos ) * 100;

    if (porcentaje >= 85 && porcentaje < 90) {
      return "#16a34a"; // verde
    }
    if ((porcentaje >= 80 && porcentaje < 85) || (porcentaje >= 90 && porcentaje < 95)) {
      return "#eab308"; // amarillo
    }
    return "#dc2626"; // rojo
  })()
}}
