fillColor:
{{
  (() => {
    const total = OperacionDia.datosFiltrados().filter(
      ot => ot.actividad_programada === "Limpieza con cliente"
    ).length || 1;

    const completadas = OperacionDia.datosFiltrados().filter(
      ot => ot.actividad_programada === "Limpieza con cliente" &&
            ot.estatus_ot === "Completada"
    ).length;

    const pct = (completadas / total) * 100;

    if (pct < 50) return "#dc2626"; // rojo
    if (pct >= 50 && pct < 75) return "#fb923c"; // naranja
    if (pct >= 75 && pct < 95) return "#eab308"; // amarillo
    return "#16a34a"; // verde
  })()
}}

progress:
{{
  (
    OperacionDia.datosFiltrados().filter(
      ot => ot.actividad_programada === "Limpieza con cliente" &&
            ot.estatus_ot === "Completada"
    ).length /
    (OperacionDia.datosFiltrados().filter(
      ot => ot.actividad_programada === "Limpieza con cliente"
    ).length || 1)
  * 100
  ).toFixed(0)
}}
