onClick:
{{
(async () => {
  // Validación del checkbox solo cuando es nuevo cliente
  if (nuevo_cliente_new?.isSwitchedOn && !Chk_entiendo_new?.isChecked) {
    showAlert("Debes aceptar la confirmación para crear un nuevo cliente.", "warning");
    return;
  }

  try {
    // Recupera la info confirmada desde store o arma un objeto desde los widgets
    const stored = appsmith.store?.nuevo_cliente_confirm || null;
    const temp = stored || {
      id_cliente: InputIdCliente_new?.text || null,
      nombre_cliente: dropdownNombreCliente_new?.selectedOptionLabel || null,
      estatus_cliente: dia_de_corte_new?.text || null,
      email_cliente: email_cliente_new?.text || null,
      celular_cliente: celular_cliente_new?.text || null,
      lista_negra: !!lista_negra_new?.isSwitchedOn,
      requiere_factura: !!requiere_factura_new?.isSwitchedOn,
      comentario_cliente: comentario_cliente_new?.text || "",
      tipo_cliente: tipo_cliente?.selectedOptionValue || null,
      // Datos fiscales
      razon_social: razon_social?.text || null,
      rfc_facturacion: rfc_facturacion?.text || null,
      domicilio_fiscal: domicilio_fiscal?.text || null,
      codigo_postal_rfc: codigo_postal_rfc?.text || null,
      regimen_fiscal: regimen_fiscal?.selectedOptionValue || null,
      uso_de_cfdi: uso_de_cfdi?.selectedOptionValue || null,
      forma_de_pago: forma_de_pago?.selectedOptionValue || null,
      metodo_de_pago: metodo_de_pago?.selectedOptionValue || null,
      notas_rfc: notas_rfc?.text || ""
    };

    // Guardar la versión confirmada por si la query la necesita desde el store
    await storeValue("nuevo_cliente_confirm", temp, true);

    // Ejecuta únicamente InsertClientesFacturacion
    if (typeof InsertClientesFacturacion === "undefined") {
      showAlert("La query InsertClientesFacturacion no está definida.", "error");
      return;
    }

    await InsertClientesFacturacion.run();

    // Éxito
    showAlert("Datos de facturación guardados correctamente.", "success");

    // Refrescar GetClientes si existe
    if (typeof GetClientes !== "undefined") {
      await GetClientes.run();
    }

    // Limpieza UI / stores
    resetWidget("Chk_entiendo_new", true);
    closeModal("Modal_confirmar_nuevo");
    closeModal("Modal_Nuevo");
    await storeValue("nuevo_cliente_temp", null, true);
    await storeValue("nuevo_cliente_confirm", null, true);
  } catch (e) {
    showAlert("Error al guardar la facturación: " + (e?.message ?? JSON.stringify(e)), "error");
  }
})();
}}
