onClick:
{{
(async () => {
  const trim = v => String(v ?? "").trim();
  const has = v => trim(v).length > 0;
  const hasSel = v => v !== undefined && v !== null && String(v).trim().length > 0;

  // Campos que pediste validar
  const missing = [];
  if (!has(email_cliente_new.text)) missing.push("Email cliente");
  if (!has(razon_social.text)) missing.push("Razón social");
  if (!has(rfc_facturacion.text)) missing.push("RFC (facturación)");
  if (!has(domicilio_fiscal.text)) missing.push("Domicilio fiscal");
  if (!has(codigo_postal_rfc.text)) missing.push("Código postal (RFC)");
  if (!hasSel(regimen_fiscal?.selectedOptionValue)) missing.push("Régimen fiscal");
  if (!hasSel(uso_de_cfdi?.selectedOptionValue)) missing.push("Uso de CFDI");
  if (!hasSel(forma_de_pago?.selectedOptionValue)) missing.push("Forma de pago");
  if (!hasSel(metodo_de_pago?.selectedOptionValue)) missing.push("Método de pago");

  if (missing.length > 0) {
    showAlert("Faltan campos obligatorios: " + missing.join(", "), "warning");
    return;
  }

  // Guardar temporalmente solo los valores relevantes (y contexto básico)
  const temp = {
    id_cliente: InputIdCliente_new?.text || null,
    nombre_cliente: dropdownNombreCliente_new?.selectedOptionLabel || null,

    // Campos validados / fiscales
    email_cliente: email_cliente_new.text,
    razon_social: razon_social.text,
    rfc_facturacion: rfc_facturacion.text,
    domicilio_fiscal: domicilio_fiscal.text,
    codigo_postal_rfc: codigo_postal_rfc.text,
    regimen_fiscal: regimen_fiscal?.selectedOptionValue,
    uso_de_cfdi: uso_de_cfdi?.selectedOptionValue,
    forma_de_pago: forma_de_pago?.selectedOptionValue,
    metodo_de_pago: metodo_de_pago?.selectedOptionValue,
    notas_rfc: notas_rfc?.text || ""
  };

  await storeValue("nuevo_cliente_temp", temp, true);

  // Abrir confirmación
  resetWidget("Modal_confirmar_nuevo", true);
  showModal("Modal_confirmar_nuevo");
})();
}}
