onClick:
{{
(async () => {
  // 1) Validación del check
  if (!Chk_entiendo?.isChecked) {
    showAlert("Debes aceptar la confirmación para continuar.", "warning");
    return;
  }

  try {
    // Evitar doble envío
    if (UpdateClientesFacturacion.isLoading || UpdateClientes.isLoading) return;

    // 2) Actualiza primero ClientesFacturacion
    await UpdateClientesFacturacion.run();

    showAlert("Datos actualizados correctamente.", "success");

    // (Opcional) refrescar vistas
    if (GetClientesFacturacion?.run) await GetClientesFacturacion.run();

    // 4) Limpia el check y cierra modales
    if (typeof Chk_entiendo?.setValue === "function") Chk_entiendo.setValue(false);
    // o: resetWidget("Chk_entiendo", true);

    closeModal("Modal_confirmar_editar");
    closeModal("Modal_Editar");
  } catch (e) {
    // Mantener modales abiertos si hay error
    showAlert("Error al guardar: " + (e?.message ?? e), "error");
  }
})()
}}
