onClick:
{{
  // 1) Limpia sesión previa
  storeValue('access_token', '');
  storeValue('refresh_token', '');
  storeValue('access_token_exp', 0);
  storeValue('last_activity', 0);

  // 2) Ejecuta login
  verify_password.run(
    () => {
      // 3) Guarda tokens y expiración
      storeValue('access_token', verify_password.data.access_token);
      storeValue('refresh_token', verify_password.data.refresh_token);
      storeValue(
        'access_token_exp',
        Math.floor(Date.now() / 1000) + verify_password.data.expires_in
      );

      // 3.1) Guarda info de usuario de Supabase
      storeValue('userInfo', {
        id_usuario: verify_password.data.user.id,
        email_usuario: verify_password.data.user.email,
        // Por ahora no tenemos rol desde Supabase, pero dejamos el campo listo
        id_rol: null,
        nombre_usuario: verify_password.data.user.email   // o más adelante un nombre real
      });

      // 4) Variables globales (centralización)
      storeValue('SUPABASE_URL', 'https://akxrdqvijwgrwttasmuk.supabase.co');
      storeValue('SUPABASE_ANON_KEY', '<TU_ANON_KEY_AQUI>');

      // 5) Marca actividad inicial (para timeout por inactividad)
      AuthUtils.ping();

      // 6) Navega a la página protegida
      showAlert('Inicio de sesión exitoso', 'success');
navigateTo("Dashboard General")
    },
    (e) => {
      showAlert('Credenciales inválidas: ' + (e?.message || ''), 'error');
    }
  );
}}
