onClick:
{{
(async () => {
  // =========================
  // 0) Reinicia el modal (defaults)
  // =========================
  resetWidget("Modal_Urgente", true);

  // =========================
  // 1) Limpia/Inicializa estado (stores, si aplica)
  // =========================
  await storeValue("ot_urgente_payload", null, true);
  await storeValue("ot_urgente_row", null, true);

  // =========================
  // 2) Radio: tipo de OT (Entrega/Recolección con cliente)
  // =========================
  if (typeof Radio_urgente?.setSelectedOption === "function") {
    Radio_urgente.setSelectedOption(null); // o "Entrega"/"Recolección" si quieres default
  }

  // =========================
  // 3) Dropdown: id_item_pedido
  // =========================
  if (typeof DropdownIdItemUrgente?.setSelectedOption === "function") {
    DropdownIdItemUrgente.setSelectedOption(null);
  }

  // =========================
  // 4) Inputs de texto/numéricos
  // =========================
  if (typeof InputIdBanoUrgente?.setValue === "function")        InputIdBanoUrgente.setValue("");
  if (typeof InputIdUbicacionUrgente?.setValue === "function")   InputIdUbicacionUrgente.setValue("");
  if (typeof InputIdRutaUrgente?.setValue === "function")        InputIdRutaUrgente.setValue("");
  if (typeof InputGeoUrgente?.setValue === "function")           InputGeoUrgente.setValue("");
  if (typeof InputNombreUbicacionUrgente?.setValue === "function") InputNombreUbicacionUrgente.setValue("");

  // =========================
  // 5) DatePicker: fecha
  // =========================
  if (typeof DatePickerUrgente?.setValue === "function") {
    DatePickerUrgente.setValue(""); // también puedes usar null
  }

  // =========================
  // 6) Texts / labels de apoyo
  // =========================
  // =========================
  // 7) Abre el modal
  // =========================
  showModal("Modal_Urgente");
})()
}}
