{{
  (() => {
    const p = appsmith.store.otPending || (appsmith.store.otIsEditMode ? appsmith.store.otSel : appsmith.store.otForm) || {};

    const tipo = p._actividad_programada || p.tipo_actividad || p.actividad_programada || "";
    const idItem = p._id_item_pedido || p.id_item_pedido || "";
    const idBano = p._id_bano || p.id_bano || "";
    const nombreUbic = p._nombre_ubicacion || p.nombre_ubicacion || "";
    const rawFecha = p._fecha_programada || p.fecha_programada || "";
		const idOT = p._id_orden_de_trabajo || p.id_orden_de_trabajo || "";
    // Intentar formatear fecha a local (si existe)
    const fecha = rawFecha ? (new Date(rawFecha)).toLocaleDateString() : "";
    const costo = p._costo_reparacion || p.costo_reparacion || "";

    return `Vas a procesar la orden con los siguientes datos:\n\nOrden de trabajo: ${idOT}\n\nActividad: ${tipo}\nID item pedido: ${idItem}\nID baño: ${idBano}\nNombre ubicación: ${nombreUbic}\nFecha programada: ${fecha}\nCosto (si aplica): ${costo}\n\nMarca la casilla y confirma para continuar.`;
  })()
}}
