onOptionChange:
{{
(async () => {
  const selectedIdItem = DropdownIdItemUrgente.selectedOptionValue;
  const pedidos = GetPedidos.data || [];

  // Buscar la fila del pedido seleccionado
  const pedidoRow = pedidos.find(p =>
    String(p.id_item_pedido) === String(selectedIdItem)
  );

  if (!pedidoRow) {
    // Limpia estado si no hay match
    storeValue(
      "otUrgente",
      {
        ...(appsmith.store.otUrgente || {}),
        id_item_pedido: selectedIdItem || null,
        id_bano: null,
        id_cliente: null,
        id_ubicacion: null,
        id_ruta: null,
        nombre_ubicacion: null,
        geolocalizacion_ubicacion: null
      },
      true
    );
    return;
  }

  // Construir objeto con los campos que necesitas
  const otUrgentePatch = {
    ...(appsmith.store.otUrgente || {}),
    id_item_pedido: pedidoRow.id_item_pedido ?? null,
    id_bano: pedidoRow.id_bano ?? null,
    id_cliente: pedidoRow.id_cliente ?? null,
    id_ubicacion: pedidoRow.id_ubicacion ?? null,
    id_ruta: pedidoRow.id_ruta ?? null,
    nombre_ubicacion: pedidoRow.nombre_ubicacion ?? null,
    geolocalizacion_ubicacion: pedidoRow.geolocalizacion_ubicacion ?? null
  };

  // Guardar en el store
  storeValue("otUrgente", otUrgentePatch, true);
})();
}}

