{{
  (() => {
    const p = appsmith.store.ot_urgente_payload || {};

    const tipo          = p.actividad_programada || "";
    const idItem        = p.id_item_pedido       || "";
    const idBano        = p.id_bano              || "";
    const nombreUbic    = p.nombre_ubicacion     || "";
    const geoUbic       = p.geolocalizacion_ubicacion || "";
    const fechaModalRaw = p.fecha_modal          || null;

    // Formatear la fecha que el usuario eligió en el DatePickerUrgente
    const fecha = fechaModalRaw
      ? (new Date(fechaModalRaw)).toLocaleDateString()
      : "";

    // Si quieres mostrar también si se usará como inicio o fin:
    const fechaInicio = p.fecha_inicio
      ? (new Date(p.fecha_inicio)).toLocaleDateString()
      : "";
    const fechaFin = p.fecha_fin
      ? (new Date(p.fecha_fin)).toLocaleDateString()
      : "";

    let lineaFechaExtra = "";
    if (fechaInicio && !fechaFin) {
      lineaFechaExtra = `\n(Se tomará como FECHA DE INICIO del pedido)`;
    } else if (!fechaInicio && fechaFin) {
      lineaFechaExtra = `\n(Se tomará como FECHA DE FIN del pedido)`;
    }

    return (
      "Vas a procesar la orden urgente con los siguientes datos:\n\n" +
      `Actividad: ${tipo}\n` +
      `ID item pedido: ${idItem}\n` +
      `ID baño: ${idBano}\n` +
      `Nombre ubicación: ${nombreUbic}\n` +
      (geoUbic ? `Geolocalización: ${geoUbic}\n` : "") +
      `Fecha seleccionada: ${fecha}${lineaFechaExtra}\n\n` +
      "Marca la casilla debajo y confirma para continuar."
    );
  })()
}}
