isDisabled:
{{
  ((appsmith.store.otIsEditMode
     ? appsmith.store.otSel?.actividad_programada
     : (appsmith.store.otForm?.actividad_programada || RadioActividad.selectedOption)
   ) === "Limpieza con cliente")
   ||
  (appsmith.store.otIsEditMode && appsmith.store.otSel?.actividad_programada === "Reparacion en almacen" && appsmith.store.otSel?.estatus_ot === "Completada")
}}

onOptionChange:
{{
  (() => {
    const val = DropdownIdBano.selectedOptionValue;
    if (appsmith.store.otIsEditMode) {
      storeValue("otSel", {...(appsmith.store.otSel || {}), id_bano: val}, true);
    } else {
      storeValue("otForm", {...(appsmith.store.otForm || {}), id_bano: val}, true);
    }
    return;
  })()
}}

sourceData:
{{
  (() => {
    const activity = appsmith.store.otIsEditMode
      ? appsmith.store.otSel?.actividad_programada
      : (appsmith.store.otForm?.actividad_programada || RadioActividad.selectedOptionValue);

    const selectedItem = appsmith.store.otIsEditMode ? appsmith.store.otSel?.id_item_pedido : appsmith.store.otForm?.id_item_pedido;

    if (activity === "Limpieza con cliente") {
      const pedidoRow = (GetPedidos.data || []).find(p => String(p.id_item_pedido) === String(selectedItem));
      if (!pedidoRow) return [];
      const banoId = pedidoRow?.id_bano;
      const banoRow = (GetInventario.data || []).find(b => String(b.id_bano) === String(banoId));
      if (banoRow) return [{ label: banoRow.nombre_ubicacion ?? banoRow.nombre_bano ?? String(banoRow.id_bano), value: banoRow.id_bano }];
      return [{ label: String(banoId), value: banoId }];
    }

    return (GetInventario.data || [])
      .filter(b => String(b.estatus_bano || "").toLowerCase() !== "colocado con cliente")
      .map(b => ({ label: b.nombre_ubicacion ?? b.nombre_bano ?? String(b.id_bano), value: b.id_bano }));
  })()
}}
