isDisabled:
{{
  (() => {
    const isEdit = appsmith.store.otIsEditMode === true;
    const activity = isEdit
      ? appsmith.store.otSel?.actividad_programada
      : (appsmith.store.otForm?.actividad_programada || RadioActividad.selectedOptionValue);
    // Disabled cuando estamos en edición o cuando la actividad no es "Limpieza con cliente"
    return isEdit || String(activity || "").trim().toLowerCase() !== "limpieza con cliente";
  })()
}}

onOptionChange:
{{
(async () => {
  // Cambia esto si tu widget tiene otro nombre
  const selected = DropdownIdItem.selectedOptionValue ?? DropdownIdItem.selectedOptionLabel;
  const pedidos = GetPedidos.data || [];

  // Buscar la fila del pedido seleccionado
  const pedidoRow = pedidos.find(p =>
    String(p.id_item_pedido) === String(selected) ||
    String(p.id_item_pedido) === String(DropdownIdItem.selectedOptionLabel)
  );

  if (!pedidoRow) {
    // Limpia campos si no hay match
    storeValue("otForm", {
      ...(appsmith.store.otForm || {}),
      id_item_pedido: selected || null,
      id_bano: null,
      id_ubicacion: null,
      id_ruta: null,
      nombre_ubicacion: null,
      geolocalizacion_ubicacion: null
    }, true);
    return;
  }

  // Construir objeto parcial para la forma
  const otFormPatch = {
    ...(appsmith.store.otForm || {}),
    id_item_pedido: pedidoRow.id_item_pedido ?? null,
    id_bano: pedidoRow.id_bano ?? null,
    id_ubicacion: pedidoRow.id_ubicacion ?? null,
    id_ruta: pedidoRow.id_ruta ?? null,
    nombre_ubicacion: pedidoRow.nombre_ubicacion ??  null,
    geolocalizacion_ubicacion: pedidoRow.geolocalizacion_ubicacion ?? null
  };

  // Guardar en appsmith.store.otForm (y lo verás en los inputs si están ligados a otForm)
  storeValue("otForm", otFormPatch, true);
})();
}}

sourceData:
{{
  // Siempre devolver la lista completa de items (labels con cliente)
  (GetPedidos.data || []).map(p => {
    const cli = (GetClientes.data || []).find(c => String(c.id_cliente) === String(p.id_cliente));
    const nombreCli = cli ? (cli.nombre_cliente || cli.nombre || "") : (p.nombre_cliente || "");
    return {
      label: `${p.id_item_pedido} — ${nombreCli}`,
      value: p.id_item_pedido,
      _bano: p.id_bano,
      _id_ubicacion: p.id_ubicacion,
      _id_ruta: p.id_ruta
    };
  });
}}
