defaultOptionValue:
{{
  (() => {
    // Si existe actividad en otSel/otForm puede venir como string o como objeto {label, value}.
    const raw = appsmith.store.otIsEditMode ? appsmith.store.otSel?.actividad_programada : appsmith.store.otForm?.actividad_programada;
    if (raw === undefined || raw === null) return "";
    return (typeof raw === "object") ? String(raw.value ?? raw.label ?? "") : String(raw);
  })()
}}

onSelectionChange:
{{
  (() => {
    const val = RadioActividad.selectedOptionValue; // <-- uso seleccionado (solo el value)
    if (appsmith.store.otIsEditMode) {
      storeValue("otSel", {...(appsmith.store.otSel || {}), actividad_programada: val}, true);
    } else {
      storeValue("otForm", {...(appsmith.store.otForm || {}), actividad_programada: val}, true);
    }
    return;
  })()
}}

