onClick:
{{
  (() => {
    storeValue("otIsEditMode", false, true);
    storeValue("otSel", null, true);

    // normalizar otForm.actividad_programada si existiera como objeto
    const curForm = appsmith.store.otForm || {};
    const act = curForm.actividad_programada;
    const actVal = (act && typeof act === "object") ? (act.value ?? act.label) : act;
    storeValue("otForm", { ...curForm, actividad_programada: actVal || null }, true);

    storeValue("otPending", null, true);
    JS_OT.clear && JS_OT.clear();
    showModal("Modal_Procesar");
  })()
}}
