sourceData:
{{
(() => {
  const selItem = id_item_pedido.selectedOptionValue;
  const pedidos = Array.isArray(GetPedidos.data) ? GetPedidos.data : (GetPedidos.data?.results || []);
  const ubics   = Array.isArray(GetUbicaciones.data) ? GetUbicaciones.data : (GetUbicaciones.data?.results || []);

  if (selItem) {
    const row = pedidos.find(r => String(r.id_item_pedido) === String(selItem));
    if (!row?.id_ubicacion) return [];
    return [{
      label: String(row.nombre_ubicacion ?? row.id_ubicacion),
      value: String(row.id_ubicacion)
    }];
  }

  // sin id_item_pedido → lista todas las ubicaciones
  return ubics.map(u => ({ label: String(u.nombre_ubicacion), value: String(u.id_ubicacion) }));
})()
}}
