isDisabled:
{{
  // Disabled si no hay cambio o el checkbox no está marcado o si hay un proceso en curso
(!Chk_confirmar_pago.isChecked)

}}

onClick:
{{
(async () => {  
    if (!Chk_confirmar_pago?.isChecked) {  
    showAlert("Debes aceptar la confirmación para continuar.", "warning");  
    return;  
  }  

  try {  
    await UpdateCobros.run();  
    showAlert("Cobro actualizado correctamente.", "success");  
    // Aquí puedes agregar refrescos o cierres de modal si quieres  

if (GetCobros?.run) await GetCobros.run();  



// Cierra modales  
closeModal("Modal_Pago");  

  } catch (e) {  
    showAlert("Error al actualiar el cobro: " + (e?.message || e), "error");  
  }  
})();


}}
