onClick:
{{
(() => {
  const faltan = [];

  // Helpers para leer valores según el tipo de widget
  const getSel  = w => (w?.selectedOptionValue ?? "").toString().trim();
  const getDate = w => (w?.selectedDate || w?.formattedDate || "").toString().trim();
  const getTxt  = w => (w?.text ?? "").toString().trim();

  // Campos a validar (los de la derecha)
  const vNombreCliente   = getSel(nombre_cliente);        // id_cliente
  const vNombreUbicacion = getSel(nombre_ubicacion);      // id_ubicacion
  const vIdBano          = getSel(id_bano);               // id_bano
  const vCategoriaBano   = getSel(categoria_bano);        // "Obra" | "Evento" | "Oficina"
  const vFrecuencia      = getSel(frecuencia_de_limpieza); // "1" | "3" | "5"
  const vFechaInicio     = getDate(fecha_inicio);
  const vMontoSinIva     = getTxt(monto_sin_iva);

  if (!vNombreCliente)   faltan.push("Nombre del cliente");
  if (!vNombreUbicacion) faltan.push("Nombre ubicación");
  if (!vIdBano)          faltan.push("ID Baño");
  if (!vCategoriaBano)   faltan.push("Categoría baño");
  if (!vFrecuencia)      faltan.push("Frecuencia limpieza");
  if (!vFechaInicio)     faltan.push("Fecha inicio");
  // Si fecha_fin debe ser obligatoria, descomenta:
  // if (!vFechaFin)        faltan.push("Fecha fin");
  if (!vMontoSinIva)     faltan.push("Monto SIN IVA");

  if (faltan.length) {
    showAlert("Faltan datos: " + faltan.join(", "), "warning");
    return;
  }

  // Si todo está completo, abre el confirm
  showModal("Confirmar_pedido");
})();
}}
