defaultDate:
{{
(() => {
const v = appsmith.store?.cliente_edit_row?.fecha_fin;
if (!v) return "";

// Si ya viene como YYYY-MM-DD, úsalo tal cual
var mYMD = String(v).match(/^(\d{4})-(\d{2})-(\d{2})$/);
if (mYMD) {
return mYMD[0];
}

// Si viene como DD/MM/YYYY o DD-MM-YYYY, conviértelo a YYYY-MM-DD
var mDMY = String(v).match(/^(\d{2})/-/-$/);
if (mDMY) {
var dd = mDMY[1], mm = mDMY[2], yyyy = mDMY[3];
return yyyy + "-" + mm + "-" + dd;
}

// Intento final: parsear con Date y regresar YYYY-MM-DD en local
var d = new Date(v);
if (!isNaN(d.getTime())) {
var yyyy2 = d.getFullYear();
var mm2 = String(d.getMonth() + 1).padStart(2, "0");
var dd2 = String(d.getDate()).padStart(2, "0");
return yyyy2 + "-" + mm2 + "-" + dd2;
}

return "";
})()
}}
