onClick:
{{
(() => {
const trim = (v) => String(v ?? "").trim();
const has = (v) => trim(v).length > 0;

// 1) Validaciones de requeridos
// Ubicación
if (!select_one?.selectedOptionValue) {
showAlert("Selecciona la ubicación.", "warning");
return;
}

// ID Baño
if (!select_four?.selectedOptionValue) {
showAlert("Selecciona el baño.", "warning");
return;
}

// Fecha inicio (date_one)
if (!has(date_one?.selectedDate)) {
showAlert("Selecciona la fecha de inicio del pedido.", "warning");
return;
}


// Precio sin IVA (input_four) > 0
const rawBase = input_four?.text ?? "";
const cleanBase = String(rawBase).replace(/,/g, "").trim();
const baseNum = Number(cleanBase);
if (!Number.isFinite(baseNum) || baseNum <= 0) {
showAlert("Captura el precio sin IVA y debe ser mayor a 0.", "warning");
return;
}

// 2) Validación adicional de fechas (opcional): fin >= inicio
const ini = new Date(date_one.selectedDate);
const fin = new Date(date_two.selectedDate);
if (!isNaN(ini.getTime()) && !isNaN(fin.getTime()) && fin < ini) {
showAlert("La fecha de recolección no puede ser anterior a la fecha de inicio.", "warning");
return;
}

// 3) Estado/Store (decisión sobre resetear)
// Recomendación: NO resetear aquí, porque el usuario puede cancelar en el modal de confirmación.
// Mantén los campos tal cual hasta que el usuario confirme o cierre sin guardar.

// 4) Abrir confirmación
resetWidget("Modal_confirmar_editar", true); // limpia solo el modal de confirmación
showModal("Modal_confirmar_editar");
})()
}}
