onClick:
{{
(async () => {
  // 0) Reinicia el modal a sus valores por defecto
  resetWidget("Modal_Nuevo", true);

  // 1) Olvida cualquier fila que se estuviera editando
  await storeValue("cliente_edit_row", null, true);

  // 2) Limpia inputs de texto
  input_one_new.setValue("");

  // 3) Limpia selects / menús
   select_one_new?.setSelectedOption(null);
  select_two_new?.setSelectedOption(null);
  select_three_new?.setSelectedOption(null);


  // 5) ¡No tocar! id_cliente y estatus_cliente se controlan con bindings

  // 6) Marca el modo "Nuevo cliente"
  switch_nuevo_new?.setDisabled(false);
  switch_nuevo_new?.setValue(true);

  // 7) Abre el modal
  showModal("Modal_Nuevo");
})();
}}
