defaultText:
{{
(() => {
const v = appsmith.store?.pedidoSel?.monto_sin_iva;
if (v === null || v === undefined || v === "") return "";
const n = Number(v);
return Number.isFinite(n) ? n.toFixed(2) : "";
})()
}}

onTextChanged:
{{
(() => {
// Lee el base desde este mismo input
const raw = input_four?.text ?? "";
// Normaliza: quita comas y espacios
const clean = String(raw).replace(/,/g, "").trim();
const base = Number(clean);

// Si no es número válido, limpia cálculos y sal
if (!Number.isFinite(base)) {
if (input_five?.setValue) input_five.setValue("");
if (input_six?.setValue) input_six.setValue("");
return;
}

// Evita negativos (opcional: coméntalo si permites descuentos negativos)
const baseOk = Math.max(0, base);

const iva = +(baseOk * 0.16).toFixed(2);
const total = +(baseOk + iva).toFixed(2);

if (input_five?.setValue) input_five.setValue(iva.toFixed(2));
if (input_six?.setValue) input_six.setValue(total.toFixed(2));
})()
}}
