{{  
(() => {  
  var inv = GetInventario.data || [];  

  // Mapea todas las opciones sin filtrar por estatus  
  var opts = inv.map(function(b) {  
    var id = (b && b.id_bano) ? String(b.id_bano) : "";  
    return { label: id, value: id };  
  });

// Ba침o actual del pedido en edici칩n
var actual = appsmith.store && appsmith.store.cliente_edit_row && appsmith.store.cliente_edit_row.id_bano
? String(appsmith.store.cliente_edit_row.id_bano).trim()
: "";

// Si el actual no est치 en disponibles, lo anteponemos
if (actual && !opts.some(function(o){ return o.value === actual; })) {
var found = inv.find(function(b){
var id = (b && b.id_bano) ? String(b.id_bano) : "";
return id === actual;
});
opts.unshift({
label: found ? (actual + " (actual)") : (actual + " (no disponible)"),
value: actual
});
}

return opts;
})()
}}
