onClick:
{{
(async () => {
  try {
    // Evita doble envío
    if (UpdatePedidos.isLoading) return;

    // Ejecuta update
    await UpdatePedidos.run();

    showAlert("Pedido actualizado correctamente.", "success");

    // Refresca data visible
    if (GetPedidos?.run) await GetPedidos.run();

    // (Opcional) Mantén el store y el input sincronizados tras guardar
    // storeValue("cliente_edit_row", { 
    //   ...(appsmith.store.cliente_edit_row || {}), 
    //   monto_sin_iva: input_four.text 
    // }, true);

    // Cierra modales
    closeModal("Modal_confirmar_editar");
    closeModal("Modal_Editar");
  } catch (e) {
    showAlert("Error al guardar: " + (e?.message ?? e), "error");
  }
})()
}}
