isDisabled:
{{
  !switch_nuevo_new.isSwitchedOn ||        // switch apagado
  !Chk_entiendo_new.isChecked ||           // no aceptó la confirmación
  InsertPedidos.isLoading                  // query corriendo
}}

onClick:
{{
(async () => {
  // Validaciones previas
  if (!switch_nuevo_new?.isSwitchedOn) {
    showAlert("El switch de nuevo cliente debe estar activado.", "warning");
    return;
  }
  if (!Chk_entiendo_new?.isChecked) {
    showAlert("Debes aceptar la confirmación para continuar.", "warning");
    return;
  }

  try {
    // Evitar doble envío
    if (InsertPedidos.isLoading || UpdateInventario.isLoading) return;

    // 1) Insertar pedido
    const pedRes = await InsertPedidos.run();
    showAlert("Pedido creado correctamente.", "success");

    // 2) Actualizar inventario
    const invRes = await UpdateInventario.run();
    // Mensaje amigable según lo que regrese tu query (ajusta si conoces el shape)
    const invMsg = invRes?.message || invRes?.status || "Inventario actualizado correctamente.";
    showAlert(invMsg, "success");

    // 3) Cerrar modal solo si todo fue OK
    closeModal("Modal_confirmar_nuevo");

    // (Opcional) refrescar listados
    // if (GetPedidos?.run) await GetPedidos.run();
    // if (GetInventario?.run) await GetInventario.run();

  } catch (e) {
    // Mantener modal abierto en caso de error
    showAlert("Error en el proceso: " + (e?.message || e), "error");
  }
})()
}}
