onClick:
{{
(async () => {
  // Utilidad: valor vacío
  const isEmpty = v =>
    v === undefined || v === null || (typeof v === "string" && v.trim() === "");

  // 1) Requeridos (ajusta etiquetas si lo deseas)
  const requeridos = [
    ["Nombre del cliente",   nombre_cliente?.selectedOptionValue],
    ["Nombre ubicación",     nombre_ubicacion?.selectedOptionValue],
    ["ID baño",              id_bano?.selectedOptionValue],
    ["Categoría baño",       categoria_bano?.selectedOptionValue],
    ["Fecha inicio",         fecha_inicio?.selectedDate],
    ["Fecha de recolección", fecha_fin?.selectedDate],
    ["Precio sin IVA",       monto_sin_iva?.text],
  ];
  const falta = requeridos.find(([label, v]) => isEmpty(v));
  if (falta) {
    showAlert(`Falta capturar: ${falta[0]}`, "warning");
    return;
  }

  // 2) Números válidos
  const sinIva = Number(monto_sin_iva?.text);
  if (Number.isNaN(sinIva) || sinIva < 0) {
    showAlert("El monto SIN IVA debe ser un número mayor o igual a 0.", "warning");
    return;
  }

  // 3) Consistencia de IDs provenientes de los selects
  if (isEmpty(id_cliente?.text)) {
    showAlert("No se pudo determinar el ID del cliente. Revisa la selección.", "warning");
    return;
  }
  if (isEmpty(id_ubicacion?.text)) {
    showAlert("No se pudo determinar el ID de la ubicación. Revisa la selección.", "warning");
    return;
  }

  // 4) Todo bien → abrimos el modal de confirmación
  resetWidget("Confirmar_cambios", true);
  showModal("Confirmar_cambios");
})()
}}
