(() => {
  const inv = GetInventario.data || [];

  // Solo los baños disponibles (normalizando espacios y mayúsculas)
  const disponibles = inv.filter(b =>
    String(b.estatus_bano || "").trim().toLowerCase() === "disponible"
  );

  // Opciones: muestra el id_bano (puedes enriquecer el label si quieres)
  const opts = disponibles.map(b => ({
    label: b.id_bano,        // ej. `${b.id_bano} · ${b.marca || ""} ${b.modelo || ""}`
    value: b.id_bano
  }));

  // ⚠️ Si estás editando un pedido y el baño actual NO está disponible,
  // opcionalmente lo incluimos para no perder la selección:
  const actual =
    (appsmith.store.inventarioSel?.id_bano ||
     appsmith.store.pedidoSel?.id_bano ||
     appsmith.store.pedidoSel?.comodinp_a ||
     "").trim();

  if (actual && !opts.some(o => o.value === actual)) {
    const found = inv.find(b => b.id_bano === actual);
    opts.unshift({
      label: found ? `${actual} (actual)` : `${actual} (no disponible)`,
      value: actual
    });
  }

  return opts;
})()
}}
