onClick:
{{
(() => {
const all = Lines.getAll ? Lines.getAll() : [];
console.log("DEBUG Lines.getAll()", all);

const hayDup = Lines.hasDuplicateBanos && Lines.hasDuplicateBanos();
if (hayDup) {
const dups = appsmith.store.__banosDuplicados || [];
const lista = dups
  .map(([a, b]) => `- Líneas ${a + 1} y ${b + 1}`)
  .join("\n");

showAlert(
  "❌ Error: No puedes asignar el mismo baño en múltiples líneas.\n" +
  (lista ? "Duplicados detectados:\n" + lista : ""),
  "error"
);
return;
}

const faltan = [];
const getValorW = (w) => {
if (!w) return "";
if (w.selectedOptionValue !== undefined && w.selectedOptionValue !== null)
return String(w.selectedOptionValue).trim();
if (w.selectedDate || w.formattedDate)
return String(w.selectedDate || w.formattedDate).trim();
if (w.text !== undefined && w.text !== null)
return String(w.text).trim();
return "";
};
const norm = (v) => (v === undefined || v === null ? "" : String(v).trim());

const clienteWidget = nombre_cliente_new;
if (!getValorW(clienteWidget)) faltan.push("Cliente");

const lineas = all;
for (let i = 0; i < lineas.length; i++) {
const l = lineas[i] || {};
const idU = norm(l.id_ubicacion_new ?? l.ubicacion_id);
const idB = norm(l.id_bano_new ?? l.bano_id);
const fch = norm(l.fecha_inicio);
const monto = norm(l.monto_sin_iva_new ?? l.precio_unitario);

if (!idU)   faltan.push(`Ubicación en línea ${i + 1}`);  
if (!idB)   faltan.push(`Baño en línea ${i + 1}`);  
if (!fch)   faltan.push(`Fecha inicio en línea ${i + 1}`);  
if (!monto) faltan.push(`Monto en línea ${i + 1}`);  

}

if (faltan.length > 0) {
console.log("DEBUG faltan:", faltan);
showAlert("Faltan datos obligatorios:\n" + faltan.join("\n"), "warning");
return;
}

showModal("Confirmar_nuevo_pedido");
})()
}}
