onTextChanged:
{{
(() => {
  const idx = currentItem.idx;
  const idU = (id_ubicacion_new.text || "").trim();

  if (!idU) {
    // Limpia la línea si borraron el ID
    Lines.update(idx, {
      id_ubicacion: null,
      nombre_ubicacion: null,
      geolocalizacion_ubicacion: null,
      id_ruta: null,
    });
    // Limpia select del nombre
    if (typeof nombre_ubicacion_new?.setSelectedOption === "function") {
      nombre_ubicacion_new.setSelectedOption(null);
    }
    return;
  }

  const ubis = GetUbicaciones.data || [];
  const u = ubis.find(x => String(x.id_ubicacion) === String(idU));

  if (u) {
    // ID válido -> setear TODO
    Lines.update(idx, {
      id_ubicacion: u.id_ubicacion,
      nombre_ubicacion: u.nombre_ubicacion,
      geolocalizacion_ubicacion: u.geolocalizacion_ubicacion ?? null,
      id_ruta: u.id_ruta ?? null,
    });
    // sincroniza el select del nombre
    if (typeof nombre_ubicacion_new?.setSelectedOption === "function") {
      nombre_ubicacion_new.setSelectedOption({ label: u.nombre_ubicacion, value: u.id_ubicacion });
    }
  } else {
    // ID no existe -> guarda el texto y limpia derivados
    showAlert("ID de ubicación no encontrado.", "warning");
    Lines.update(idx, {
      id_ubicacion: idU,
      nombre_ubicacion: null,
      geolocalizacion_ubicacion: null,
      id_ruta: null,
    });
    if (typeof nombre_ubicacion_new?.setSelectedOption === "function") {
      nombre_ubicacion_new.setSelectedOption(null);
    }
  }
})()
}}
