defaultOptionValue:
{{ ((options, serverSideFiltering) => ( 
(() => {
const nombre = currentItem?.nombre_ubicacion_new;
if (!nombre) return "";
const cat = GetUbicaciones.data || [];
const found = cat.find(x => String(x.nombre_ubicacion) === String(nombre));
return found ? String(found.id_ubicacion) : "";
})()
))(nombre_ubicacion_new.options, nombre_ubicacion_new.serverSideFiltering) }}

onOptionChange:
{{
(() => {
const idx = currentItem?.idx;
if (idx === undefined) return;

const value = nombre_ubicacion_new?.selectedOptionValue; // id_ubicacion
const label = nombre_ubicacion_new?.selectedOptionLabel; // nombre_ubicacion

if (!value) {
Lines.update(idx, {
nombre_ubicacion_new: null,
id_ubicacion_new: null,
geolocalizacion_ubicacion_new: null,
id_ruta_new: null,
});
return;
}

const cat = GetUbicaciones.data || [];
const found = cat.find(x => String(x.id_ubicacion) === String(value)) || {};

Lines.update(idx, {
nombre_ubicacion_new: String(label ?? found.nombre_ubicacion ?? ""),
id_ubicacion_new: String(value),
geolocalizacion_ubicacion_new: found.geolocalizacion_ubicacion ?? null,
id_ruta_new: found.id_ruta ?? null,
});
})()
}}
