onClick:
{{
(() => {
  /* 1) Limpia TODOS los stores que ese modal usa como defaults */
  storeValue("pedidoSel", null, true);
  storeValue("clienteSel", null, true);
  storeValue("ubicacionSel", null, true);
  storeValue("inventarioSel", null, true);
  storeValue("lines", [], true);         // limpia las líneas del pedido anterior

  /* 2) Utilidades de limpieza de widgets */
  const clearSelect = (w) => {
    if (!w) return;
    if (typeof w.setSelectedOption === "function") { w.setSelectedOption(null); return; }
    if (typeof w.setValue === "function") w.setValue("");
  };
  const clearInput = (w) => { if (w?.setValue) w.setValue(""); };
  const clearDate  = (w) => { if (w?.setValue) w.setValue(null); else if (w?.setDate) w.setDate(null); };

  /* 3) Limpia los widgets globales del modal */
  clearSelect(nombre_cliente_new);
  clearSelect(nombre_ubicacion_new_global);
  clearInput(id_cliente_new);
  clearInput(id_ubicacion_new_global);

  /* 4) Inicia con 1 baño por defecto */
  if (typeof num_banos?.setValue === "function") num_banos.setValue("1");
  Lines.init(1);  // genera una línea vacía automáticamente

  /* 5) Limpia el List por seguridad */
  resetWidget("lstLineas", true);

  /* 6) Abre el modal limpio */
  resetWidget("Modal_nuevo_pedido", true);
  showModal("Modal_nuevo_pedido");
})();
}}
