onClick:
{{
(async () => {
  // 1) Campos obligatorios (sin email)
  const req = [
    { label: "Nombre ubicación",    val: String(nombre_ubicacion.text ?? "").trim() },
    { label: "Nombre contacto",     val: String(nombre_contacto_ubicacion.text ?? "").trim() },
    { label: "Cel contacto",        val: String(celular_contacto_ubicacion.text ?? "").trim() },
    { label: "ID ruta",             val: String(id_ruta.selectedOptionValue ?? "").trim() },
    { label: "Geolocalización",     val: String(geolocalizacion_ubicacion.text ?? "").trim() },
    { label: "Dirección ubicación", val: String(direccion_ubicacion.text ?? "").trim() },
  ];

  const faltantes = req.filter(f => f.val.length === 0);
  if (faltantes.length) {
    showAlert("Completa: " + faltantes.map(f => f.label).join(", "), "warning");
    return;
  }

  // 2) Normalizaciones seguras (todo a string primero)
  const celLimpio = String(celular_contacto_ubicacion.text ?? "")
    .replace(/[^0-9]/g, ""); // solo dígitos

  const email = String(email_contacto_ubicacion.text ?? "").trim().toLowerCase();

  // 3) Payload
  const payload = {
    id_ubicacion:                 (String(id_ubicacion.text ?? "").trim() || null),
    nombre_ubicacion:             String(nombre_ubicacion.text ?? "").trim(),
    nombre_contacto_ubicacion:    String(nombre_contacto_ubicacion.text ?? "").trim(),
    celular_contacto_ubicacion:   celLimpio,
    email_contacto_ubicacion:     email, // opcional: validar formato solo si no está vacío
    id_ruta:                      String(id_ruta.selectedOptionValue ?? "").trim(),
    geolocalizacion_ubicacion:    String(geolocalizacion_ubicacion.text ?? "").trim(),
    direccion_ubicacion:          String(direccion_ubicacion.text ?? "").trim(),
    colonia_ubicacion:            (colonia_ubicacion?.text != null ? String(colonia_ubicacion.text).trim() : null),
    codigo_postal_ubicacion:      (codigo_postal_ubicacion?.text != null ? String(codigo_postal_ubicacion.text).trim() : null),
    comentario_ubicacion:         (comentario_ubicacion?.text != null ? String(comentario_ubicacion.text).trim() : null),
  };

  await storeValue("ubicacion_edit_payload", payload, true);
  resetWidget("Modal_confirmar_editar", true);
  showModal("Modal_confirmar_editar");
})();
}}
