onClick:
{{
(async () => {
  // 1) Campos obligatorios (los del recuadro verde)
  const req = [
    { label: "Nombre ubicación",        val: nombre_ubicacion.text },
    { label: "Nombre contacto",         val: nombre_contacto_ubicacion.text },
    { label: "Cel contacto",            val: celular_contacto_ubicacion.text },
    { label: "Email contacto",          val: email_contacto_ubicacion.text },
    { label: "ID ruta",                 val: id_ruta.selectedOptionValue },
    { label: "Geolocalización",         val: geolocalizacion_ubicacion.text },
    { label: "Dirección ubicación",     val: direccion_ubicacion.text },
  ];

  // 2) ¿Hay faltantes?
  const faltantes = req.filter(f => f.val === null || f.val === undefined || String(f.val).trim() === "");
  if (faltantes.length) {
    showAlert("Completa: " + faltantes.map(f => f.label).join(", "), "warning");
    return;
  }

  // 3) Arma el payload con limpieza básica
  const payload = {
    id_ubicacion:        id_ubicacion.text || null,  // opcional si lo tienes en el modal
    nombre_ubicacion:    nombre_ubicacion.text.trim(),
    nombre_contacto_ubicacion: nombre_contacto_ubicacion.text.trim(),
    celular_contacto_ubicacion: (celular_contacto_ubicacion.text || "").replace(/[^0-9]/g, ""),
    email_contacto_ubicacion:   (email_contacto_ubicacion.text || "").trim().toLowerCase(),
    id_ruta:             String(id_ruta.selectedOptionValue).trim(),
    geolocalizacion_ubicacion: (geolocalizacion_ubicacion.text || "").trim(),
    direccion_ubicacion: (direccion_ubicacion.text || "").trim(),

    // Campos no obligatorios pero útiles si los quieres guardar en el store:
    colonia_ubicacion:        colonia_ubicacion?.text?.trim?.() || null,
    codigo_postal_ubicacion:  codigo_postal_ubicacion?.text?.trim?.() || null,
    comentario_ubicacion:     comentario_ubicacion?.text?.trim?.() || null,
  };

  // 4) Guarda para el confirm
  await storeValue("ubicacion_edit_payload", payload, true);

  // 5) Abre el modal de confirmación
  resetWidget("Modal_confirmar_editar", true);
  showModal("Modal_confirmar_editar");
})();
}}
