(async () => {
  try {
    const p = appsmith.store.ubicacion_edit_payload || {};
    if (!p || !p.id_ubicacion) {
      showAlert("No hay datos para actualizar.", "warning");
      return;
    }

    // Si tu query usa parámetros, pásalos aquí:
    await UpdateUbicaciones.run({
      _id_ubicacion:               p.id_ubicacion,
      _nombre_ubicacion:           p.nombre_ubicacion,
      _nombre_contacto_ubicacion:  p.nombre_contacto_ubicacion,
      _telefono_contacto_ubicacion:p.celular_contacto_ubicacion,
      _email_contacto_ubicacion:   p.email_contacto_ubicacion,
      _id_ruta:                    p.id_ruta,
      _geolocalizacion_ubicacion:  p.geolocalizacion_ubicacion,
      _direccion_ubicacion:        p.direccion_ubicacion,
      _colonia_ubicacion:          p.colonia_ubicacion,
      _codigo_postal_ubicacion:    p.codigo_postal_ubicacion,
      _comentario_ubicacion:       p.comentario_ubicacion
    });

    showAlert("Ubicación actualizada correctamente.", "success");

    // refresca la tabla (opcional)
    GetUbicaciones.run();

    // vuelve al modal anterior
    closeModal("Modal_confirmar_editar");
    showModal("Modal_Procesar"); // si tu modal se llama distinto, cámbialo aquí
  } catch (e) {
    showAlert("Error al actualizar: " + (e?.message ?? e), "error");
  }
})();
}}
